    $logo = 'images/logo.png';
    list($largura) = getimagesize($logo);
    include('config.php');
    require_once("libs/dataBaseClass.php");
    
    $message = "";

    if(isset($_GET["id"]) 
        && !empty($_GET["id"]) 
        && strlen($_GET["id"]) == 32){

        $db = new DataBase();
        $conn = $db->connect();
    
        $sql = "
            SELECT * FROM usuarios 
            WHERE fb_cod_exclusao = '{$_GET["id"]}'
            LIMIT 1
        ";
        $query = mysqli_query($conn, $sql);
        $user = mysqli_fetch_assoc($query);

        if(count($user) == 0){
            $message = "Não foi possível localizar seus dados. Suas informações já foram excluídas ou seu código de confirmação é inválido.";
        } else if(empty($user["fb_token_extendido"])){
            $message = "Seus dados foram excluídos com sucesso.";
        } else {
            $message = "Ocorreu um erro inesperado, tente novamente. Se o problema persistir entre em contato com nosso suporte técnico.";
        }
        
    } else {
        $message = "Código de confirmação inválido.";
        $_GET["id"] = "código inválido";
    }
    
?>

